CREATE TABLE IF NOT EXISTS users(userId VARCHAR(40) UNIQUE NOT NULL,email VARCHAR(40) UNIQUE NOT NULL, password VARCHAR(256) NOT NULL);
CREATE TABLE IF NOT EXISTS company(id VARCHAR(40) UNIQUE NOT NULL,name VARCHAR(40) NOT NULL,personName VARCHAR(40) NOT NULL, email VARCHAR(40) NOT NULL,address VARCHAR(256) NOT NULL,product VARCHAR(40) NOT NULL);
CREATE TABLE IF NOT EXISTS onboard(id VARCHAR(40) UNIQUE NOT NULL,vendorName VARCHAR(40) NOT NULL,constitutionVendor VARCHAR(40) NOT NULL, field VARCHAR(40) NOT NULL,natureOfScope VARCHAR(256) NOT NULL,contactPersonId VARCHAR(256) NOT NULL);